mapper.select:
select t1.id,t1.watchname,t1.fixprice,t1.status,t1.user_address useraddress,t1.user_name username,t1.user_tel usertel,t2.type_name typename,t3.brand_name brandname from watch t1 left join type t2 on t1.type=t2.id left join brand t3 on t1.brand=t3.id where 1=1
and t1.watchname like '%${watchname}%'
and t1.type =#{typeId}
and t1.brand =#{brandId}
order by t1.id desc

mapper.insert:
insert into watch (id, watchname, brand,
  type, fixprice, status, 
  user_address, user_name, user_tel
  )
values (#{id,jdbcType=INTEGER}, #{watchname,jdbcType=VARCHAR}, #{brand,jdbcType=INTEGER},
  #{type,jdbcType=INTEGER}, #{fixprice,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, 
  #{userAddress,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR}, #{userTel,jdbcType=VARCHAR}
  )

mapper.select:
select id from watch
#{id}
and status in (0,2,3,4,5)

mapper.update:
update watch
set status=1
where id = #{id,jdbcType=INTEGER}

mapper.select:
select t1.id,t1.watchname,t3.brand_name brandname ,t2.type_name typename,t1.fixprice,t1.status,t1.user_name username from watch t1 left join type t2 on t1.type=t2.id left join brand t3 on t1.brand=t3.id where t2.id=#{typeId} and t1.status=#{status}
and t1.watchname like '%${watchname}%'
and t1.username like '%${username}%'
and t1.brand =#{brandId}
order by t1.id desc
